import type { Span, StartSpanOptions } from "@sentry/types";
export interface Sentry {
    startSpan<T>(context: StartSpanOptions, callback: (span: Span) => T): T;
}
export default class SentryHandler {
    sentry: Sentry | null;
    constructor(sentry?: Sentry);
    startSpan<T>(context: StartSpanOptions, callback: (span: Span) => T): T;
}
