export interface AuthBody {
  signatures: string[];
  session?: string;
}

export interface SignRequest extends AuthBody {
  session: string;
  msg: string;
  player_index: number;
  parties: number[];
  pubkey: string;
  endpoints: string[];
  share_coefficient?: string;
}

export interface SignResponse {
  sig: string;
}

export interface SendRequestBody {
  session: string;
  sender: number;
  recipient: number;
  msg_type: string;
  msg_data: string;
}
